#include<iostream>
using namespace std;

long long max_13(string x){
    long long mul=1;
    long long max=0;
    for(long long i=0;i<x.length()-12;i++){
        long long n0,n1,n2,n3,n4,n5,n6,n7,n8,n9,n10,n11,n12;
        n0=x[i+0]-'0';
        n1=x[i+1]-'0';
        n2=x[i+2]-'0';
        n3=x[i+3]-'0';
        n4=x[i+4]-'0';
        n5=x[i+5]-'0';
        n6=x[i+6]-'0';
        n7=x[i+7]-'0';
        n8=x[i+8]-'0';
        n9=x[i+9]-'0';
        n10=x[i+10]-'0';
        n11=x[i+11]-'0';
        n12=x[i+12]-'0';
        mul=n0*n1*n2*n3*n4*n5*n6*n7*n8*n9*n10*n11*n12;
        if(mul>max){
            max=mul;
        }
    }
    return max;
}




int main(){
    string a="";

    cout<<max_13(a);
    return 0;
}